import React from "react";
import { Card, CardHeader, CardContent, Grid } from '@material-ui/core';

function Book(props) {
  console.log("BOOK PROPS", props);
  return (
    <Card>
      <CardHeader>
      <Button type="submit" variant="contained" color="secondary" {...props}>
        {props.children}
      </Button>
      <Button type="submit" variant="contained" color="secondary" {...props}>
        {props.children}
      </Button>
      </CardHeader>
      <CardContent>
        <Grid container spacing={3}>
          <Grid item xs={3}>
            <img src={props.image} alt="book thumbnail"></img>
          </Grid>
          <Grid item xs={9}>
            <h3>{props.title}</h3>
            <h5>Authors: {props.author}</h5>
            {/* <h5>Authors: {props.author.forEach((author, index) => (
              author + ", "
            ))}</h5> */}
            <p><strong>Description:</strong> {props.description}</p> 
            <a href={props.infoLink} target="_blank" rel="noopener noreferrer">More Information</a>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
}

export default Book;
