import React from "react";
import { Card, CardHeader, CardContent, Grid, ButtonGroup, Button } from '@material-ui/core';
import API from "../pages/utils/API";
// import { Link } from "react-router-dom";

function Book(props) {

  function handleSaveClick(event) {
    event.preventDefault();
    API.saveBook(props);
  }

  console.log("BOOK PROPS", props);
  return (
    <Card>
      <CardHeader
        action={
          <ButtonGroup color="secondary">
            <a href={props.infoLink} target="_blank" rel="noopener noreferrer">
              <Button variant="contained" color="default">{props.leftButton}</Button>
            </a>
            <Button variant="contained" color="default" onClick={e => props.handleSaveClick(e, id)}>{props.rightButton}</Button>
          </ButtonGroup>
        } />
        {props.children}
      <CardContent>
        <Grid container spacing={3}>
          <Grid item xs={3}>
            <img src={props.image} alt="book thumbnail"></img>
          </Grid>
          <Grid item xs={9}>
            <h3>{props.title}</h3>
            <h5>Authors: {props.author}</h5>
            {/* <h5>Authors: {props.author.forEach((author, index) => (
              author + ", "
            ))}</h5> */}
            <p><strong>Description:</strong> {props.description}</p> 
            <a href={props.infoLink} target="_blank" rel="noopener noreferrer">More Information</a>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
}

export default Book;
