import React, { useState, useEffect } from "react";
import Results from "../components/Results";
import Book from "../components/Book";
import { Input, FormBtn, } from "../components/Form"
import API from "./utils/API";

function Search() {
  const [search, setSearch] = useState();
  const [formObject, setFormObject] = useState()

  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
    console.log("handleInputChange: ", value);
  }

  useEffect(() => {

  })

  function handleFormSubmit(event) {
    // console.log("EVENT:", event);
    event.preventDefault();
    if(formObject.title) {
      API.getGoogleBooks({title: formObject.title}) // want to render the list of books
      .then(response => {
        console.log("RESPONSE", response)
        // what do I do here? how can I pass the results to the UI?
        this.setState({search: response}); // cannot read property setState of undefined
      })
      .catch(err => console.log("ERROR", err));
    }
  }

  return (
    <div className="search-page">
      <header className="search-page-header">
      </header>
      <div className="book-search">
        {/* <Title></Title> */}
        {/* <SearchForm></SearchForm> */}
        <form>
            <Input
              onChange={handleInputChange}
              name="title"
              placeholder="Search for a book by title"
            />
            <FormBtn
              // disabled={!(formObject.title)}
              onClick={handleFormSubmit}
            >
              Submit Book
            </FormBtn>
          </form>
        <Book></Book>
        <Results
          title={search}
        ></Results>
      </div>
    </div>
  );
}

export default Search;
