import React, { useState } from 'react';
// import logo from './logo.svg';
import API from "./pages/utils/API";
import './App.css';

const [appState, setAppState] = useState(0);

function Book(props) {
  return(
    <div>
      <h3>{props.title}</h3>
      <h4>{props.subtitle}</h4>
      <h5>Authors: {props.authors}</h5>
      <div><img src={props.image} alt="temp"></img></div>
      <div>{props.info}</div>
    </div>
  )
}

function App() {
  let title;
  // authors={response.data.items[0].volumnInfo.authors}
  let image;
  let info;
  API.getGoogleBooks()
    .then(response => {
      console.log("response:", response);
      title=response.data.items[0].volumeInfo.title
      // authors={response.data.items[0].volumnInfo.authors}
      image=response.data.items[0].volumeInfo.imageLinks.thumbnail
      info=response.data.items[0].volumeInfo.imageLinks.description
    })
  return (
    <div className="App">
      <header className="App-header">
      </header>
      <div className="results">
          <Book
            title={title}
            image={image}
            info={info}
          >
          </Book>
      </div>
    </div>
  );
}

export default App;
